class Grafo:
    def __init__(self, matriz):
        self.matriz = matriz
        self.n = len(matriz)

    def mostrar_conexoes(self):
        for i in range(self.n):
            for j in range(self.n):  # Agora percorre toda a matriz, garantindo simetria
                if self.matriz[i][j] == 1:
                    print(f"{i} {j}")

